import UIKit

protocol ChatListPresentationLogic
{
  func presentSomething(response: ChatList.Something.Response)
}

class ChatListPresenter: ChatListPresentationLogic
{
  weak var viewController: ChatListDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: ChatList.Something.Response)
  {
    let viewModel = ChatList.Something.ViewModel()
    viewController?.displaySomething(viewModel: viewModel)
  }
}
